import React from "react";
import { useContext, useState, useCallback, useEffect } from "react";
import ContextSocket from "../context/context-socketio";

function ComponentLayer() {
  const Socket = useContext(ContextSocket);
  const [message, setMessage] = useState("");
  const [temp, setTemp] = useState(0);
  const [voltage, setVoltage] = useState(0);

  const onReceiveMessage = useCallback(
    (receivedMessage) => {
      setTemp(JSON);
      setMessage(receivedMessage);
    },
    [setMessage]
  );

  useEffect(() => {
    if (Socket != null) Socket.on("sendPic_message", onReceiveMessage);
    if (temp != null) setTemp(temp);
    if (voltage != null) setVoltage(voltage);
  }, [Socket, temp, voltage]);

  return (
    <div>
      <h1>{message}</h1>
    </div>
  );
}

export default ComponentLayer;
